package com.example.studentapp.databaseimport androidx.room.Daoimport androidx.room.Deleteimport androidx.room.Insertimport androidx.room.Queryimport com.example.deasa12.database.database.SingerInfo@Daointerface UserDao {    @Query("SELECT * FROM singers;")    fun getAll(): MutableList<SingerInfo>    @Query("SELECT NOT EXISTS(SELECT * FROM singers WHERE name = :name )")    fun isNotExists(name:String?):Boolean    @Insert    fun insertData(singer: SingerInfo)    @Delete    fun delete(art: SingerInfo)    @Query("DELETE FROM singers")    fun deleteAll()}